// SPDX-License-Identifier: GPL-3.0

pragma solidity ^0.8.7;

contract ChainRegistrarOnSubchain {
    struct ValidatorAddrSharePair {
        address validator;
        uint256 shareAmount;
    }

    uint256 public numBlocksPerDynasty;
    uint256 public crossChainFee;
    address public admin;
    address public fallbackReceiver; // fallback address in case sending token to the specified receiver failed

    constructor(
        uint256 numBlocksPerDynasty_,
        uint256 crossChainFee_,
        address admin_,
        address fallbackReceiver_
    ) {
        numBlocksPerDynasty = numBlocksPerDynasty_;
        crossChainFee = crossChainFee_;
        admin = admin_;
        fallbackReceiver = fallbackReceiver_;
    }

    function getDynasty() external view returns (uint256, bool) {
        bytes memory data = new bytes(0);
        (bool success, bytes memory result) = address(0xb4).staticcall(data); // 0xb4 for the "dynasty" precompiled contract
        require(
            success,
            "failed to call precompiled contract to query the current dynasty"
        );

        uint256 dynasty = abi.decode(result, (uint256));
        return (dynasty, true);
    }

    function getCrossChainFee() external view returns (uint256) {
        return crossChainFee;
    }

    function getFallbackReceiver() external view returns (address) {
        return fallbackReceiver;
    }

    function isARegisteredSubchain(uint256) external pure returns (bool) {
        return true; // TODO: Implement a new precompile to perform the check? Or is it OK to always return true on a for the chain registrar on a subchain
    }

    function getSubchainRegistrationHeight(uint256)
        external
        pure
        returns (uint256, bool)
    {
        return (0, false); // not actually used by subchain token banks
    }

    function getNumBlocksPerDynasty() external view returns (uint256) {
        return numBlocksPerDynasty;
    }

    function getValidatorSet(uint256 subchainID, uint256 dynasty)
        external
        view
        returns (address[] memory validators, uint256[] memory shareAmounts)
    {
        bytes memory data = new bytes(32 + 32);
        bytes32 suchainIDBytes = bytes32(subchainID);
        bytes32 dynastyBytes = bytes32(dynasty);
        uint256 idx = 0;
        for (uint256 i = 0; i < 32; i++) {
            data[idx++] = suchainIDBytes[i];
        }
        for (uint256 i = 0; i < 32; i++) {
            data[idx++] = dynastyBytes[i];
        }
        (bool success, bytes memory result) = address(0xb5).staticcall(data); // 0xb5 for the "validatorSet" precompiled contract
        require(
            success,
            "staticcall to the validatroSet precompiled contract failed"
        );

        ValidatorAddrSharePair[] memory pairs = abi.decode(
            result,
            (ValidatorAddrSharePair[])
        );
        uint256 numValidators = pairs.length;
        validators = new address[](numValidators);
        shareAmounts = new uint256[](numValidators);
        for (uint256 i = 0; i < numValidators; i++) {
            validators[i] = pairs[i].validator;
            shareAmounts[i] = pairs[i].shareAmount;
        }

        return (validators, shareAmounts);
    }

    function updateCrossChainFee(uint256 newCrossChainFee) external adminOnly {
        crossChainFee = newCrossChainFee;
    }

    function updateFallbackReceiver(address newFallbackReceiver)
        external
        adminOnly
    {
        fallbackReceiver = newFallbackReceiver;
    }

    function updateAdmin(address newAdmin) external adminOnly {
        admin = newAdmin;
    }

    //
    // Modifiers
    //

    modifier adminOnly() {
        require(msg.sender == admin, "Only the admin can make this call");
        _;
    }
}
